import hashlib
TRANSCRIPTS_IDX = 6

def remove_chosen(content, to_remove=["#incomplete","#incompelet","#incomplet"]):
    for chosen in to_remove:
        content = [line.replace(chosen, ' ') for line in content]
    return content

def get_unique_words(transcript_content):
    sorted = list(set(' '.join(transcript_content).split(' ')))
    sorted.sort()
    return sorted

def get_unique_chars(transcript_content):
    sorted = list(set(' '.join(transcript_content)))
    sorted.sort()
    return sorted

def create_fingerprint(parameter_set):
    final_fingerprint = []
    
    for param in parameter_set:
        if isinstance(param, str):
            final_fingerprint.append(param)
        elif isinstance(param, bool):
            final_fingerprint.append(str(param))
        elif isinstance(param, int):
            final_fingerprint.append(str(param))
        elif isinstance(param, float):
            final_fingerprint.append(str(param))
        elif isinstance(param, list):
            final_fingerprint.append(' '.join([str(i) for i in param]))
            
    return final_fingerprint

def hash_fingerprint(fingerprint):
    return hashlib.md5(fingerprint.encode())

def read_input(file_path, preferred_type=None):
    if not preferred_type:
        if "kws.phxstm" in file_path:
            return read_kws(file_path)
        elif "stt.phxstm" in file_path:
            return read_stm(file_path)
        else:
            raise Exception("Source file {} is invalid. Make sure to use either kws.phxstm or stt.phxstm\n".format(file_path))
    else:
        if preferred_type == "file":
            return remove_newlines(read_file(file_path))
        elif preferred_type == "stm":
            return read_stm(file_path)
        elif preferred_type == "kws":
            return read_kws(file_path)
        else:
            raise Exception("Preferred type {} is invalid\n".format(preferred_type))
    
def read_stm(stm_path):
    content = read_file(stm_path)
    content = remove_newlines(content)
    content = choose_by_idx(split_by_delim(content), TRANSCRIPTS_IDX)
    return content

def read_kws(kws_path):
    content = read_file(kws_path)
    content = remove_newlines(content)
    return content

def read_file(file_path):
    with open(file_path, 'r') as in_f:
        content = in_f.readlines()
    return content

# remove underscores for spelling and bracketed words
def trim_input(content):
    for i in range(0,len(content)):
        single_line = content[i]
        single_line = remove_spelling(single_line)
        single_line = to_lower(single_line)
        #single_line = remove_bracketed_words(single_line)
        single_line = adjust_spacing(single_line)
        content[i] = single_line
    return content

def to_lower(line):
    return line.lower()

def adjust_spacing(line):
    line = re.sub(r' +$','', line)
    line = re.sub(r'^ +','', line)
    line = re.sub(r' +',' ', line)
    return line

def remove_spelling(line):
    line = re.sub(r'_','',line)
    return line

def remove_bracketed_words(line):
    line = re.sub(r'\[.*?\]','',line)
    line = re.sub(r'<.*?>','',line)
    return line

def remove_newlines(content):
    return [line.strip('\n') for line in content]

def split_by_delim(content, delim='\t'):
    return [line.split(delim) for line in content]

def choose_by_idx(content, idx):
    return [line[idx] for line in content]

def unique_elements(content):
    return list(set(content))

