def create_ngrams(sentences, n=3):
    ngram_results = []

    for sentence in sentences:
        sentence = sentence.split(' ')

        for i in range(0, len(sentence) - n + 1):
            ngram_results.append(tuple(sentence[i:i+n]))

    return ngram_results